%% Fit: 'surface fit rxn kinetics'.
[xData, yData, zData] = prepareSurfaceData( BM, TemperatureK, Times );

% Set up fittype and options.
ft = fittype( 'a*exp(-b/y)*(x^c)', 'independent', {'x', 'y'}, 'dependent', 'z' );
opts = fitoptions( 'Method', 'NonlinearLeastSquares' );
opts.Display = 'Off';
opts.StartPoint = [0.445586200710899 0.646313010111265 0.709364830858073];

% Fit model to data.
[fitresult, gof] = fit( [xData, yData], zData, ft, opts );

% Plot fit with data.
